import React, { useState } from 'react';
import CodeEditor from './components/CodeEditor';
import OutputTabs from './components/OutputTabs';
import Header from './components/Header';
import './App.css';

const App = () => {
  const [explanation, setExplanation] = useState('');
  const [debuggingTips, setDebuggingTips] = useState('');
  const [challenge, setChallenge] = useState('');

  const handleSubmit = async (code) => {
  try {
    const response = await fetch("http://127.0.0.1:5000/api/parse-code", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });

    const data = await response.json();
    console.log("Backend Response:", data); // Log backend response

    if (data.error) {
      setExplanation(data.error);
      setDebuggingTips("No tips available.");
      setChallenge("No challenge available.");
    } else {
      setExplanation(data.explanation || "No explanation available.");
      setDebuggingTips("No tips yet.");
      setChallenge("No challenges generated.");
    }
  } catch (err) {
    console.error("Error:", err);
    setExplanation("Failed to connect to backend.");
    setDebuggingTips("No tips available.");
    setChallenge("No challenge available.");
  }
};
};

export default App;
